import shortId from "shortid";

export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "제로초",
      },
      content: "첫번째 게시글 #헤시태그 #익스프레스",
      Images: [
        {
          id: shortId.generate(),
          src:
            "https://cdn.pixabay.com/photo/2020/05/11/20/27/monkey-5160145_960_720.jpg",
        },
        {
          id: shortId.generate(),
          src:
            "https://cdn.pixabay.com/photo/2020/07/21/00/46/skateboarder-5424692_960_720.jpg",
        },
        {
          id: shortId.generate(),
          src:
            "https://cdn.pixabay.com/photo/2020/07/17/06/41/puppy-5413165_960_720.jpg",
        },
      ],
      Comments: [
        {
          id: shortId.generate(),
          User: {
            id: shortId.generate(),
            nickname: "nero",
          },
          content: "우와 개정판",
        },
        {
          id: shortId.generate(),
          User: {
            id: shortId.generate(),
            nickname: "hello",
          },
          content: "데미갓!",
        },
      ],
    },
  ],
  imagePaths: [],
  addpostLoading: false,
  addpostDone: false,
  addpostError: null,
  removepostLoading: false,
  removepostDone: false,
  removepostError: null,
  addCommenttLoading: false,
  addCommenttDone: false,
  addCommenttError: null,
};

export const ADD_POST_REQUEST = "ADD_POST_REQUEST";
export const ADD_POST_SUCCESS = "ADD_POST_SUCCESS";
export const ADD_POST_FAILURE = "ADD_POST_FAILURE";

export const REMOVE_POST_REQUEST = "REMOVE_POST_REQUEST";
export const REMOVE_POST_SUCCESS = "REMOVE_POST_SUCCESS";
export const REMOVE_POST_FAILURE = "REMOVE_POST_FAILURE";

export const ADD_COMMENT_REQUEST = "ADD_COMMENT_REQUEST";
export const ADD_COMMENT_SUCCESS = "ADD_COMMENT_SUCCESS";
export const ADD_COMMENT_FAILURE = "ADD_COMMENT_FAILURE";

export const addPost = (data) => ({
  type: ADD_POST_REQUEST,
  data,
});
export const addComment = (data) => ({
  type: ADD_COMMENT_REQUEST,
  data,
});

const dummypost = (data) => ({
  id: data.id,
  content: data.content,
  User: {
    id: 1,
    nickname: "제로초",
  },
  Images: [],
  Comments: [],
});

const dummyComment = (data) => ({
  id: shortId.generate(),
  content: data,
  User: {
    id: 1,
    nickname: "제로초",
  },
});

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST_REQUEST:
      return {
        ...state,
        addpostLoading: true,
        addpostDone: false,
        addpostError: null,
      };
    case ADD_POST_SUCCESS:
      return {
        ...state,
        mainPosts: [dummypost(action.data), ...state.mainPosts],
        addpostDone: true,
        addpostLoading: false,
      };

    case ADD_POST_FAILURE:
      return {
        ...state,
        addpostLoading: false,
        addpostError: action.error,
      };

    case REMOVE_POST_REQUEST:
      return {
        ...state,
        removepostLoading: true,
        removepostDone: false,
        removepostError: null,
      };
    case REMOVE_POST_SUCCESS:
      return {
        ...state,
        mainPosts: state.mainPosts.filter((v) => v.id !== action.data),
        removepostDone: true,
        removepostLoading: false,
      };

    case REMOVE_POST_FAILURE:
      return {
        ...state,
        removepostLoading: false,
        removepostError: action.error,
      };

    case ADD_COMMENT_REQUEST:
      return {
        ...state,
        addCommentLoading: true,
        addCommentDone: false,
        addCommentError: null,
      };
    case ADD_COMMENT_SUCCESS: {
      const postIndex = state.mainPosts.findIndex(
        (v) => v.id === action.data.postId
      );
      const post = state.mainPosts[postIndex];
      const Comments = [dummyComment(action.data.content), ...post.Comments];
      const mainPosts = [...state.mainPosts];
      mainPosts[postIndex] = { ...post, Comments };
      return {
        ...state,
        mainPosts,
        addCommentDone: true,
        addCommentLoading: false,
      };
    }
    case ADD_COMMENT_FAILURE:
      return {
        ...state,
        addCommentLoading: false,
        addCommentError: action.error,
      };

    default:
      return state;
  }
};

export default reducer;
