export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "제로초",
      },
      content: "첫번째 게시글 #헤시태그 #익스프레스",
      Images: [
        {
          src:
            "https://cdn.pixabay.com/photo/2020/05/11/20/27/monkey-5160145_960_720.jpg",
        },
        // {
        //   src:
        //     "https://cdn.pixabay.com/photo/2020/07/21/00/46/skateboarder-5424692_960_720.jpg",
        // },
        // {
        //   src:
        //     "https://cdn.pixabay.com/photo/2020/07/17/06/41/puppy-5413165_960_720.jpg",
        // },
      ],
      Comments: [
        {
          User: {
            nickname: "nero",
          },
          content: "우와 개정판",
        },
        {
          User: {
            nickname: "hello",
          },
          content: "데미갓!",
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = "ADD_POST";

export const addPost = {
  type: ADD_POST,
};

const dummypost = {
  id: 2,
  content: "더미데이터입니다",
  User: {
    id: 1,
    nickname: "제로초",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummypost, ...state.mainPosts],
        postAdded: true,
      };
    default:
      return state;
  }
};

export default reducer;
